package com.jlpay.ext.qrcode.contants;

import com.jlpay.ext.qrcode.trans.constants.ExtConstants;

/**
 * @author zhaoyang2
 * 交易常量
 */
public class TransContants {

	//交易请求地址
	public static final String tradeUrl = "https://qrcode-dev.jlpay.com/api/pay/";
	//接入方系统私钥
	public static final String sysPriKey = "";
	//嘉联公钥
	public static final String jlPubKey = "";
	
	static public void setJlpayProperty() {
		System.setProperty(ExtConstants.orgPrivateKey, TransContants.sysPriKey);
		System.setProperty(ExtConstants.jlpayPublicKey,TransContants.jlPubKey);
		System.setProperty(ExtConstants.tradeUrl, TransContants.tradeUrl);
	}
}
